var three = require('three');

three.Object3D.prototype.copyTransforms = function copyTransforms(otherNode) {
	this.quaternion.copy(otherNode.quaternion);
	this.rotation.copy(otherNode.rotation);
	this.position = otherNode.position;
	this.matrix.copy(otherNode.matrix);
	this.matrixWorld.copy(otherNode.matrixWorld);
}