var three = require('three');
var BaseGun = require('./BaseGun');

function BallMaker(world, pos) {
	var geometry = new THREE.CylinderGeometry(0.035, 0.365, 1, 16, 1);
	BaseGun.call(this, world, pos, geometry);
	this.primaryFire = primaryFire.bind(this);
}

BallMaker.prototype = Object.create(BaseGun.prototype);

function primaryFire(pos, playerSize) {
	this.world.makeBall(pos, playerSize);
}

module.exports = BallMaker;