var three = require('three');
var geomLib = require('geometry/lib');
var matLib = require('materials/lib');
var BaseGun = require('./BaseGun');

function BallMaker(pos) {
	var geometry = geomLib.gunBase(0.035, 0.365, 1, 16, 1);
	var material = matLib.checkerboards.green();
	BaseGun.call(this, pos, geometry, material);
	this.primaryFireStart = primaryFireStart.bind(this);
}

BallMaker.prototype = Object.create(BaseGun.prototype);

function primaryFireStart(pos, playerSize) {
	this.player.world.makeBall(pos, playerSize);
}

module.exports = BallMaker;