var three = require('three');
var cannon = require('cannon');
var BaseGun = require('./BaseGun');

function HitScanExploder(world, pos) {
	var geometry = new THREE.CylinderGeometry(0.035, 0.065, 1.5, 16, 1);
	BaseGun.call(this, world, pos, geometry);
	this.primaryFire = primaryFire.bind(this);
}

HitScanExploder.prototype = Object.create(BaseGun.prototype);

var relative = new three.Vector3();
var result = new cannon.RaycastResult();
function primaryFire(pos, playerSize) {
	var from = new three.Vector3();
	var to = new three.Vector3();
	relative.subVectors(this.player.crosshair.localToWorld(to), this.player.camera.localToWorld(from));
	relative.multiplyScalar(100);
	to.add(relative);

    this.world.world.raycastClosest(from, to, {
        skipBackfaces: true
    }, result);
    if(result.hasHit) {
    	this.world.makeHitEffect(result.hitPointWorld, playerSize);
    	if(result.body.type == cannon.Body.DYNAMIC) {
    		result.body.applyImpulse(relative, result.hitPointWorld);
    	}
    }
}

module.exports = HitScanExploder;