var three = require("three");
var geomLib = require("geometry/lib");
var matLib = require("materials/lib");
var BaseGun = require("./BaseGun");

function SharpShooter(pos) {
	var geometry = geomLib.gunBase(0.065, 0.065, 1, 8, 1);
	var material = matLib.checkerboards.green();
	BaseGun.call(this, pos, geometry, material);
	this.primaryFireStart = primaryFireStart.bind(this);
}

SharpShooter.prototype = Object.create(BaseGun.prototype);

var vel = new three.Vector3();
function primaryFireStart(pos, playerSize) {
	var zero = new three.Vector3();
	var zero2 = new three.Vector3();
	vel.subVectors(this.player.user.crosshair.localToWorld(zero2), this.player.user.camera.localToWorld(zero));
	vel.multiplyScalar(100);
	this.player.world.makeBall(pos, playerSize * 0.2, vel);
}

module.exports = SharpShooter;