var three = require("three");
var CheckerboardTexture = require("threejs-texture-checkerboard");

var __mats = {};
function checkerboards(colorA = 0x000000, colorB = 0xffffff, cols = 4, rows = 4) {
	var hash = Array.prototype.join.call(arguments, ";");
	if(!__mats[hash]) {
		__mats[hash] = new three.MeshPhongMaterial({
			map: new CheckerboardTexture(colorA, colorB, cols, rows)
		});
	}

	return __mats[hash];
}


checkerboards.red = function() {
	return checkerboards(0x7f0000, 0xff7f7f);
};

checkerboards.green = function() {
	return checkerboards(0x007f00, 0x7fff7f);
};

checkerboards.blue = function() {
	return checkerboards(0x003f7f, 0x7fafff);
};


module.exports = checkerboards;