function SoftNumberToggle(valTrue, valFalse, speedTrue, speedFalse) {
	this.value = valFalse;
	this.valTrue = valTrue;
	this.valFalse = valFalse;
	this.valRange = valTrue - valFalse;
	this.speedTrue = speedTrue;
	this.speedFalse = speedFalse;
	this.state = false;

	this.onEnterFrame = onEnterFrame.bind(this);
}


function onEnterFrame(timeScale) {
	this.value = Math.clamp(this.value + (this.state?this.speedTrue:-this.speedFalse) * timeScale, this.valFalse, this.valTrue);
}

module.exports = SoftNumberToggle;