var three = require("three");
var geomLib = require("geometry/lib");


function decorateMeshWithBodyTemps(mesh, body) {
	for (var i = body.shapes.length - 1; i >= 0; i--) {
		var shape = body.shapes[i];
		var geometry = geomLib.sphere(1, 32, 16);
		var testSphere = new three.Mesh(geometry, new three.MeshBasicMaterial({wireframe: true, color: 0xffffff}));
		var s = shape.radius;
		var p = body.shapeOffsets[i];
		testSphere.scale.set(s, s, s);
		testSphere.position.set(p.x, p.y, p.z);
		mesh.add(testSphere);
	}

}

module.exports = decorateMeshWithBodyTemps;